		 										  " WHERE D.DISTRICT_ID =M.DIST_ID AND T.TEHSIL_ID=M.TEHSIL_ID  AND P.VAL_TXN_ID = M.VAL_TXN_ID AND A.AREA_TYPE_ID = M.AREA_TYPE_ID AND SA.SUB_AREA_TYPE_ID = M.SUB_AREA_TYPE_ID AND WP.WARD_PATWARI_ID = M.WARD_PATWARI_ID AND C.COLONY_ID = M.COLONY_ID AND P.VAL_TXN_ID=?";

	
