	public static final String GET_BUILDING_VIEW_DETAILS= "SELECT DISTINCT D.DISTRICT_NAME,T.TEHSIL_NAME,A.AREA_TYPE_NAME,SA.SUB_AREA_TYPE_NAME, "+
	  														" WP.WARD_PATWARI_NAME,C.COLONY_NAME,B.BUILDING_TXN_ID,B.BUILDING_SUB_TYPE_ID,B.FLOOR_AREA,F.FLOOR_NAME,B.PROP_OLD_20,B.PROP_OLD_50,B.LIFT_PRESENT,"+
	  														" B.TRANSACT_OPEN_TERRACE,(SELECT PROP_TYPE_L1_NAME FROM IGRS_PROP_TYPE_L1_MASTER_DUMMY WHERE PROP_TYPE_L1_ID=B.TERRACE_USAGE),B.TERRACE_AREA,"+
	  														" (SELECT PROP_TYPE_L1_NAME FROM IGRS_PROP_TYPE_L1_MASTER_DUMMY WHERE PROP_TYPE_L1_ID=B.MULTISTOREY_SUB_TYPE),B.TOTAL_AREA,"+
	  														" B.BUILD_UP_AREA,B.COMMON_AREA,(SELECT PROPERTY_TYPE_L2_NAME FROM IGRS_PROP_TYPE_L2_MASTER_DUMMY WHERE PROPERTY_TYPE_L2_ID=B.COMMERCIAL_PROP_TYPE)"+
	  														",B.PROP_IN_MALL,B.PROP_NEAR_ROAD,B.RESIDENTIAL_AREA,B.COMMERCIAL_AREA,B.INDUSTRIAL_AREA,B.EDUCATION_AREA,B.HEALTH_AREA,B.OTHER_AREA,B.EDUCATION_TCP,B.HEALTH_TCP,B.IS_CONSTRUCTION,B.IS_AKVN,B.IS_HOUSING_BOARD,B.MULTISTOREY_SUB_TYPE,B.RESI_COMM_AREA"+
	  														" FROM IGRS_PROP_BUILDING_DETLS B,IGRS_FLOOR_MASTER F,IGRS_DISTRICT_MASTER D,IGRS_PROP_VAL_TXN_DETLS M,IGRS_TEHSIL_MASTER T,IGRS_AREA_TYPE_MASTER A,IGRS_SUB_AREA_TYPE_MASTER SA,IGRS_WARD_PATWARI_MASTER WP,IGRS_COLONY_MASTER C "+
	  														" WHERE D.DISTRICT_ID =M.DIST_ID AND T.TEHSIL_ID=M.TEHSIL_ID  AND B.VAL_TXN_ID = M.VAL_TXN_ID AND A.AREA_TYPE_ID = M.AREA_TYPE_ID AND SA.SUB_AREA_TYPE_ID = M.SUB_AREA_TYPE_ID AND WP.WARD_PATWARI_ID = M.WARD_PATWARI_ID AND C.COLONY_ID = M.COLONY_ID AND F.FLOOR_ID = NVL(B.PROP_AT_FLOOR,3) AND B.VAL_TXN_ID=?";
	
	
	public static final String GET_BUILDING_VIEW_DETAILS_HINDI= "SELECT DISTINCT D.H_DISTRICT_NAME,T.H_TEHSIL_NAME,A.H_AREA_TYPE_NAME,SA.H_SUB_AREA_TYPE_NAME, "+
		" WP.H_WARD_PATWARI_NAME,C.H_COLONY_NAME,B.BUILDING_TXN_ID,B.BUILDING_SUB_TYPE_ID,B.FLOOR_AREA,F.H_FLOOR_NAME,B.PROP_OLD_20,B.PROP_OLD_50,B.LIFT_PRESENT,"+
		" B.TRANSACT_OPEN_TERRACE,(SELECT H_PROP_TYPE_L1_NAME FROM IGRS_PROP_TYPE_L1_MASTER_DUMMY WHERE PROP_TYPE_L1_ID=B.TERRACE_USAGE),B.TERRACE_AREA,"+
		"(SELECT H_PROP_TYPE_L1_NAME FROM IGRS_PROP_TYPE_L1_MASTER_DUMMY WHERE PROP_TYPE_L1_ID=B.MULTISTOREY_SUB_TYPE),B.TOTAL_AREA,"+
		" B.BUILD_UP_AREA,B.COMMON_AREA,(SELECT H_PROPERTY_TYPE_L2_NAME FROM IGRS_PROP_TYPE_L2_MASTER_DUMMY WHERE PROPERTY_TYPE_L2_ID=B.COMMERCIAL_PROP_TYPE)"+
		",B.PROP_IN_MALL,B.PROP_NEAR_ROAD,B.RESIDENTIAL_AREA,B.COMMERCIAL_AREA,B.INDUSTRIAL_AREA,B.EDUCATION_AREA,B.HEALTH_AREA,B.OTHER_AREA,B.EDUCATION_TCP,B.HEALTH_TCP,B.IS_CONSTRUCTION,B.IS_AKVN,B.IS_HOUSING_BOARD,B.MULTISTOREY_SUB_TYPE,B.RESI_COMM_AREA"+
		" FROM IGRS_PROP_BUILDING_DETLS B,IGRS_FLOOR_MASTER F,IGRS_DISTRICT_MASTER D,IGRS_PROP_VAL_TXN_DETLS M,IGRS_TEHSIL_MASTER T,IGRS_AREA_TYPE_MASTER A,IGRS_SUB_AREA_TYPE_MASTER SA,IGRS_WARD_PATWARI_MASTER WP,IGRS_COLONY_MASTER C "+
		" WHERE D.DISTRICT_ID =M.DIST_ID AND T.TEHSIL_ID=M.TEHSIL_ID  AND B.VAL_TXN_ID = M.VAL_TXN_ID AND A.AREA_TYPE_ID = M.AREA_TYPE_ID AND SA.SUB_AREA_TYPE_ID = M.SUB_AREA_TYPE_ID AND WP.WARD_PATWARI_ID = M.WARD_PATWARI_ID AND C.COLONY_ID = M.COLONY_ID  AND F.FLOOR_ID = NVL(B.PROP_AT_FLOOR,3) AND B.VAL_TXN_ID=?";




	  
