    public static final String SUBCLAUSEPROPDISPLAY="SELECT iptmt.PROPERTY_TYPE_ID,iptmt.PROPERTY_TYPE_NAME,"
+" iptl1mt.PROPERTY_TYPE_L1_ID,iptl1mt.PROPERTY_TYPE_L1_NAME,"
+" iptl2mt.PROPERTY_TYPE_L2_ID,NVL(iptl2mt.PROPERTY_TYPE_L2_NAME,'-')" 
 +" FROM IGRS_PROPERTY_TYPE_MASTER iptmt"
   +" LEFT OUTER JOIN IGRS_PROP_TYPE_L1_MASTER iptl1mt ON"
    +" (iptl1mt.PROPERTY_TYPE_ID=iptmt.PROPERTY_TYPE_ID) and iptmt.PROPERTY_TYPE_STATUS='A'" 
    		+" AND iptl1mt.PROPERTY_TYPE_L1_STATUS='A'"
     +" LEFT OUTER JOIN IGRS_PROP_TYPE_L2_MASTER iptl2mt ON"
     +" (iptl2mt.PROPERTY_TYPE_L1_ID=iptl1mt.PROPERTY_TYPE_L1_ID) and iptl2mt.PROPERTY_TYPE_L2_STATUS='A'"
     +" WHERE iptmt.PROPERTY_TYPE_ID=? ORDER BY"
    +" iptl1mt.PROPERTY_TYPE_L1_ID,iptl2mt.PROPERTY_TYPE_L2_ID";
    
    public static final String SUBCLAUSEPROPDISPLAY_H="SELECT iptmt.PROPERTY_TYPE_ID,iptmt.H_PROPERTY_TYPE_NAME,"
    	+" iptl1mt.PROPERTY_TYPE_L1_ID,iptl1mt.H_PROPERTY_TYPE_L1_NAME,"
    	+" iptl2mt.PROPERTY_TYPE_L2_ID,NVL(iptl2mt.H_PROPERTY_TYPE_L2_NAME,'-')" 
    	 +" FROM IGRS_PROPERTY_TYPE_MASTER iptmt"
    	   +" LEFT OUTER JOIN IGRS_PROP_TYPE_L1_MASTER iptl1mt ON"
    	    +" (iptl1mt.PROPERTY_TYPE_ID=iptmt.PROPERTY_TYPE_ID) and iptmt.PROPERTY_TYPE_STATUS='A'" 
    	    		+" AND iptl1mt.PROPERTY_TYPE_L1_STATUS='A'"
    	     +" LEFT OUTER JOIN IGRS_PROP_TYPE_L2_MASTER iptl2mt ON"
    	     +" (iptl2mt.PROPERTY_TYPE_L1_ID=iptl1mt.PROPERTY_TYPE_L1_ID) and iptl2mt.PROPERTY_TYPE_L2_STATUS='A'"
    	     +" WHERE iptmt.PROPERTY_TYPE_ID=? ORDER BY"
    	    +" iptl1mt.PROPERTY_TYPE_L1_ID,iptl2mt.PROPERTY_TYPE_L2_ID";
 
    public static final String SELECT_SUB_CLAUSE_NEW="SELECT S.SUB_CLAUSE_ID,S.SUB_CLAUSE_NAME,S.SUB_CLAUSE_DESC,S.SUB_CLAUSE_STATUS FROM IGRS_SUB_CLAUSE_MASTER S WHERE S.SUB_CLAUSE_ID=?";
    
    public static final String SELECT_SUB_CLAUSE_NEW1="select distinct iscm.SUB_CLAUSE_ID,SUB_CLAUSE_NAME,SUB_CLAUSE_DESC,SUB_CLAUSE_STATUS," 
    		+" OPERATOR_NAME,OPERAND,DEFINE_FOR_ALL_DISTRICT,USER_DEFINED_FIELD,NORMAL_VAL,APP_FACTOR_AREA,APP_FACTOR_VALUE,AREA_BASED_DIV"
+" from IGRS_SUB_CLAUSE_MASTER iscm,IGRS_COMPUTATION_LOGIC_MASTER iclm right outer join IGRS_OPERATOR_MASTER iom"
+" on iom.OPERATOR_ID=iclm.OPERATOR_ID where iclm.SUB_CLAUSE_ID=iscm.SUB_CLAUSE_ID and iclm.SUB_CLAUSE_ID=?"; 
    
    public static final String VIEWPROPFORSUBCLAUSE="SELECT distinct iptmt.PROPERTY_TYPE_NAME," 
    		+" iptl1mt.PROPERTY_TYPE_L1_NAME,NVL(iptl2mt.PROPERTY_TYPE_L2_NAME,'-'),iptmt.H_PROPERTY_TYPE_NAME,iptl1mt.H_PROPERTY_TYPE_L1_NAME,NVL(iptl2mt.H_PROPERTY_TYPE_L2_NAME,'-')"
 +" FROM IGRS_SUB_CLAUSE_MASTER iscm,IGRS_SUBCLAUSE_PROP_MAPPING ispm" 
 +" left outer join IGRS_PROPERTY_TYPE_MASTER iptmt on (ispm.PROPERTY_TYPE_ID=iptmt.PROPERTY_TYPE_ID)" 
 +" left outer join IGRS_PROP_TYPE_L1_MASTER iptl1mt on (ispm.PROPERTY_TYPE_L1_ID=iptl1mt.PROPERTY_TYPE_L1_ID)" 
 +" left outer join IGRS_PROP_TYPE_L2_MASTER iptl2mt on (ispm.PROPERTY_TYPE_L2_ID=iptl2mt.PROPERTY_TYPE_L2_ID)" 
 +" where ispm.SUB_CLAUSE_ID=iscm.SUB_CLAUSE_ID and iscm.SUB_CLAUSE_ID=?"; 
    
    public static final String UPDATE_SUB_CLAUSE_NEW="UPDATE IGRS_SUB_CLAUSE_MASTER S SET S.SUB_CLAUSE_DESC=?,S.SUB_CLAUSE_STATUS=?,S.UPDATED_BY=?,S.UPDATED_DATE=SYSDATE WHERE S.SUB_CLAUSE_ID=?";
    public static final String SELECT_SUB_CLAUSE_MASTER_LIST_NEW="SELECT S.SUB_CLAUSE_ID,S.SUB_CLAUSE_NAME,S.SUB_CLAUSE_DESC FROM IGRS_SUB_CLAUSE_MASTER S WHERE S.SUB_CLAUSE_STATUS='A' ORDER BY SUB_CLAUSE_ID ";
    
    public static final String SELECT_COMPUTATION_DETAILS="select distinct iom.operator_name, iclm.OPERAND,"
+" iclm.APP_FACTOR_OPERATOR_ID ,iclm.APP_FACTOR_OPERAND,iclm.COMP_BASED_ON_L1,iclm.COMP_BASED_ON_L2"
+" from igrs_operator_master iom , igrs_computation_logic_master iclm,IGRS_SUB_CLAUSE_MASTER isclmt" 
+" where iom.operator_id=(select operator_id from igrs_computation_logic_master"
+" where computation_logic_id=(select computation_logic_id from IGRS_SUB_CLAUSE_MASTER" 
+" where sub_clause_id=?)) and iclm.computation_logic_id = (select computation_logic_id from"
+" IGRS_SUB_CLAUSE_MASTER  where sub_clause_id=?)"
+" and isclmt.COMPUTATION_LOGIC_ID=iclm.COMPUTATION_LOGIC_ID";
