    public static final String GET_SUBCLAUSE_PROP_DETLS = "SELECT DISTINCT pt.PROPERTY_TYPE_NAME, NVL(spt.PROP_SUB_TYPE_NAME,'-') ,NVL(ptl1.PROP_TYPE_L1_NAME,'-'),pt.H_PROPERTY_TYPE_NAME," +
    		"NVL(spt.H_PROP_SUB_TYPE_NAME,'-'),NVL(ptl1.H_PROP_TYPE_L1_NAME,'-'),ispm.PROPERTY_TYPE_ID,ispm.SUB_PROPERTY_TYPE,NVL(ispm.PROPERTY_TYPE_L1_ID,0) FROM " +
    		"IGRS_SUB_CLAUSE_MASTER iscm,IGRS_SUBCLAUSE_PROP_MAPPING ispm LEFT OUTER JOIN IGRS_PROPERTY_TYPE_MASTER pt ON " +
    		"(ispm.PROPERTY_TYPE_ID=pt.PROPERTY_TYPE_ID) LEFT OUTER JOIN IGRS_PROP_SUB_TYPE_MASTER spt ON " +
    		"(ispm.SUB_PROPERTY_TYPE=spt.PROP_SUB_TYPE_ID) LEFT OUTER JOIN IGRS_PROP_TYPE_L1_MASTER_DUMMY ptl1 ON" +
    		" (ispm.PROPERTY_TYPE_L1_ID=ptl1.PROP_TYPE_L1_ID) WHERE " +
    		"ispm.SUB_CLAUSE_ID=iscm.SUB_CLAUSE_ID AND iscm.SUB_CLAUSE_ID= ?";
    
