		//String transDate1=paymentBD.gettranDate(eForm);
        SimpleDateFormat date1 = new SimpleDateFormat ("MM/dd/yyyy HH:mm:ss");
        SimpleDateFormat date2 = new SimpleDateFormat ("dd/MM/yyyy hh:mm:ss a");
