	//to get applicant type id 
	
	public ArrayList getappId(String TempId, String languageLocale){
ArrayList list1=commonBd.getappId(TempId);
		
		ArrayList	list  =  new ArrayList();
	      if (list1 != null) {
	          for (int i  =  0; i < list1.size(); i++) {
	        	  ArrayList lst  =  (ArrayList)list1.get(i);
	        	  
	        	  RegCommonDTO dto= new RegCommonDTO();
	        	  
	        	// Modified by Gulrej on 24th May // for addition of data in hindi
	        	  
	        	  if("en".equalsIgnoreCase(languageLocale)){
	        		  dto.setApplicantTypeEfile(lst.get(0).toString());
	        	  }
	        	  else{
	        		  dto.setApplicantTypeEfile(lst.get(5).toString()); 
	        	  }
	        	  dto.setPartyTypeEfile(lst.get(1).toString());
	        	  
	        	// Modified by Gulrej on 24th May // for addition of data in hindi
	        	  if("en".equalsIgnoreCase(languageLocale)){
	        		  dto.setPartyIdEfile(lst.get(2).toString());
	        	  }else{
	        		  dto.setPartyIdEfile(lst.get(4).toString());
	        	  }
	        	  dto.setIdparty(lst.get(3).toString());
	        	  
	              list.add(dto);
	          }
	      }
		return list;
	}
	
