/**
 * getTransPartyDetsParticular
 * for getting transacting party details corresponding to a particular ID from db.
 * @param String
 * @return ArrayList
 * @author SHREERAJ
 */
public ArrayList getTransPartyDetsParticular(String partId, String appId, String languageLocale, int deedId, int inst) throws Exception {
	
	
	ArrayList list=commonBd.getTransPartyDetsParticular(partId,appId);
	ArrayList finalList=new ArrayList();
	ArrayList row_list=new ArrayList();
	String row;
	String rowArr[];
	CommonDTO dto;
	if(list != null && list.size()>0){
	for(int i=0;i<list.size();i++){
		row_list=(ArrayList)list.get(i);
		
		row=row_list.toString();
		row=row.substring(1, row.length()-1);
		rowArr=row.split(",");
		
		dto=new CommonDTO();
		//dto.setRole(rowArr[0].trim());
		
		dto.setRole(getRoleName(rowArr[5].trim(),languageLocale,deedId,inst));
		
		if(!(rowArr[1].trim().equals("")) && !(rowArr[1].trim().equals("null"))){
			dto.setName(rowArr[1].trim()+" "+rowArr[2].trim());
		}
		else if(!(rowArr[3].trim().equals("")) && !(rowArr[3].trim().equals("null"))){
			dto.setName(rowArr[3].trim());
		}
		dto.setId(rowArr[4].trim());
		dto.setRoleId(rowArr[5].trim());
		finalList.add(dto);
	}
	return finalList;
	}
	else{
		return null;
	}
}

