public ArrayList<CommonDTO> createPartyMapList(ArrayList partyList,RegCompletionForm form,String languageLocale) throws Exception {
	
	ArrayList<CommonDTO> finalPartyList=new ArrayList<CommonDTO>();
	ArrayList rowList=new ArrayList();
	CommonDTO dto;
	if(partyList!=null && partyList.size()>0){
		
		for(int i=0;i<partyList.size();i++){
			rowList=(ArrayList)partyList.get(i);
			dto=new CommonDTO();
			
			//dto.setId(rowList.get(0).toString());
			
			dto.setRole(getRoleName(rowList.get(5).toString(),languageLocale,form.getDeedID(),form.getInstID()));
			
			if(rowList.get(1)!=null  && !(rowList.get(1).toString().equals("")) && !(rowList.get(1).toString().equals("null"))){
				dto.setName(rowList.get(1).toString()+" "+rowList.get(2).toString());
			}
			else if(rowList.get(3)!=null && !(rowList.get(3).toString().equals("")) && !(rowList.get(3).toString().equals("null"))){
				dto.setName(rowList.get(3).toString());
			}
			dto.setId(rowList.get(4).toString());
			dto.setRoleId(rowList.get(5).toString());
			
			
			
			finalPartyList.add(dto);
			
		}
		
		
		
	}
	
	
	
	return finalPartyList;

}

