public String landMappingScreen(RegCommonForm regForm, String languageLocale) {
	
	
String forward="";
	 
	 //land on mapping screen
	 try{
	 int count=getPropertyCount(regForm.getHidnRegTxnId());
	 
	 if(regForm.getPvReqFlag().equalsIgnoreCase("y") || regForm.getPropReqFlag().equalsIgnoreCase("y"))
	 {		
	if (count==1 || regForm.getDeedID()==RegInitConstant.DEED_EXCHANGE || 
			regForm.getDeedID()==RegInitConstant.DEED_PARTITION_NPV || 
			regForm.getAppStatus().equalsIgnoreCase(RegInitConstant.STATUS_BACKED_MAPPING_SAVED)){             				
	
		
		regForm.setPropWithMapping(getAllPartiesPropertiesAlreadyMap(regForm,languageLocale));
		regForm.setPropWithoutMapping(null);
		regForm.setDeclareShareSize(regForm.getPropWithMapping().size());     					
		
		if(regForm.getPvReqFlag().equalsIgnoreCase("y")){
			forward ="alreadyMapped";
			}
		else{           				
			forward ="alreadyMappedPropNonPV";
			}             					
		
		regForm.setCheckParty("true");
		//return mapping.findForward(forward);
	
}else{
	
	regForm.setPropWithMapping(null);
	regForm.setPropWithoutMapping(getAllPartiesPropertiesMap(regForm,languageLocale));             				
	regForm.setDeclareShareSize(regForm.getPropWithoutMapping().size());		
	
	
	if(regForm.getPvReqFlag().equalsIgnoreCase("y")){
		forward ="getMapping";
		}
	else{                				
			forward ="getMappingPropNonPV";
		                    				
		} 				
	
	}
	
}else if(regForm.getCommonDeed()==1){
	 
	 int particularCount=getParticularCount(regForm.getHidnRegTxnId());
	 if(particularCount>0){
		 regForm.setParticularWithoutMapping(getAllPartiesParticularsMap(regForm,languageLocale));
	 forward ="getMappingParticularNonPV";
	 }else{
		 forward ="failure";
	 }
	 
	 
}
	 
	 }catch(Exception e){
		 logger.debug(e.getMessage(),e);
		 forward="failure";
	 }
	
	 return (forward);
	 
	
	
	
	
}
