public boolean generateHash(String file1Path, String file2Path, RegCompletDTO dto) throws Exception{
	
	String hashText1="";
	String hashText2="";
    
    try{
    	
        MessageDigest mdObj=MessageDigest.getInstance("MD5");  
        
        
        InputStream file1=new FileInputStream(file1Path);
        InputStream file2=new FileInputStream(file2Path);
        DigestInputStream digest1=new DigestInputStream(file1,mdObj);  //getting stream
        DigestInputStream digest2=new DigestInputStream(file2,mdObj);  //getting stream
        
        
       // byte[] buf1=new byte[4096];  			//setting buffer
        while (digest1.read()!=-1);  			//reading and updating message digest
        byte[] output1=mdObj.digest();  		//getting hash data in bytes
        BigInteger bi1=new BigInteger(1,output1);
        hashText1=bi1.toString(16);  			//getting hex value of hash
        digest1.close();  						//closing stream
        
       // byte[] buf=new byte[4096];  			//setting buffer
        while (digest2.read()!=-1);  			//reading and updating message digest
        byte[] output2=mdObj.digest();  		//getting hash data in bytes
        BigInteger bi2=new BigInteger(1,output2);
        hashText2=bi2.toString(16);  			//getting hex value of hash
        digest2.close();  						//closing stream
        
        
        if(hashText1!=null && hashText2!=null && !hashText1.equalsIgnoreCase("") && !hashText2.equalsIgnoreCase("")){
        	
        	dto.setHash1(hashText1);
        	dto.setHash2(hashText2);
        	return true;
        	
        }else{
        	dto.setHash1(null);
        	dto.setHash2(null);
        	return false;
        	
        }
        
     }catch(Exception e){
    	dto.setHash1(null);
     	dto.setHash2(null);
     	logger.debug(e.getMessage(),e);
      return false;
     }
	
}

