    public static final String IGRS_NOENCUM_VIEW_ENTIRE_DETAILS=" SELECT pd.PARTY_FIRST_NAME,pd.PARTY_MIDDLE_NAME," +
    		" pd.PARTY_LAST_NAME,pd.PARTY_GENDER,  decode(nvl(pd.PARTY_AGE,0),0,' ',pd.PARTY_AGE), pd.COUNTRY_ID,  " +
    		" (SELECT COUNTRY_NAME FROM IGRS_COUNTRY_MASTER WHERE COUNTRY_ID=pd.COUNTRY_ID),  STATE_ID," +
    		" (SELECT STATE_NAME FROM IGRS_STATE_MASTER WHERE STATE_ID=pd.STATE_ID),   pd.DISTRICT_ID," +
    		" (SELECT DISTRICT_NAME FROM IGRS_DISTRICT_MASTER   WHERE DISTRICT_ID=pd.DISTRICT_ID)," +
    		" pd.PARTY_ADDRESS,decode(nvl(pd.POSTAL_CODE,0),0,' ',pd.POSTAL_CODE),  " +
    		" pd.PHONE_NUMBER,pd.MOBILE_NUMBER,pd.EMAIL_ID, pd.PHOTO_PROOF_TYPE_ID,  " +
    		" (SELECT PHOTO_PROOF_TYPE_NAME FROM IGRS_PHOTOID_PROOF_TYPE_MASTER  WHERE PHOTO_PROOF_TYPE_ID=pd.PHOTO_PROOF_TYPE_ID),  " +
    		" pd.PHOTO_PROOF_ID,pd.BANK_NAME,pd.BANK_ADDRESS,pd.GUARDIAN_NAME,   pd.MOTHER_NAME,pd.CASTE_ID,(SELECT CASTE_NAME   " +  
    		" FROM IGRS_CASTE_MASTER WHERE CASTE_ID=pd.CASTE_ID),  pd.RELIGION_ID,  " +
    		" (SELECT RELIGION_NAME FROM IGRS_RELIGION_MASTER   WHERE RELIGION_ID=pd.RELIGION_ID),  " +
    		" pd.NATIONALITY,  TO_CHAR(pd.CREATED_DATE,'DD/MM/YYYY'),  " +
    		" txn.PURPOSE_OF_REQUEST ,  " +
    		" (SELECT PROPERTY_TYPE_NAME FROM IGRS_PROPERTY_TYPE_MASTER  WHERE PROPERTY_TYPE_ID=pr.PROPERTY_TYPE_ID) PROP_TYPE, " +
    		" pr.VIKAS_KHAND,  " +
    		" (SELECT DISTRICT_NAME FROM IGRS_DISTRICT_MASTER   WHERE DISTRICT_ID=pr.DISTRICT_ID) PROP_DIST,  " +
    		" (SELECT TEHSIL_NAME FROM IGRS_TEHSIL_MASTER  WHERE TEHSIL_ID=pr.TEHSIL_ID) PROP_TEHSIL,  " +
    		" (SELECT WARD_PATWARI_NAME FROM IGRS_WARD_PATWARI_MASTER  WHERE WARD_PATWARI_ID=pr.WARD_PATWARI_ID) PROP_PATWARI,  " +
    		" (SELECT MOHALLA_VILLAGE_NAME FROM IGRS_MOHALLA_VILLAGE_MASTER  WHERE MOHALLA_VILLAGE_ID=pr.MOHALLA_VILLAGE_ID) PROP_VILLAGE,  " +
    		" (SELECT MUNICIPAL_BODY_NAME FROM IGRS_MUNICIPAL_BODY_MASTER  WHERE MUNICIPAL_BODY_ID=pr.MUNCIPAL_BODY_TYPE_ID) PROP_MUNICIPALTY,  " +
    		" (SELECT AREA_TYPE_NAME FROM IGRS_AREA_TYPE_MASTER  WHERE AREA_TYPE_ID=pr.AREA_TYPE_ID) AREA_TYPE,  " +
    		" txn.REGISTRATION_ID ,  " +
    		" to_char(txn.CREATED_DATE,'DD/MM/YYYY'),  " +
    		" pr.RI_CIRCLE,  " +
    		" pr.LAYOUT_DETAIL,  " +
    		" pr.KHASRA_NUMBER,  " +
    		" pr.NAZOOL_SHEET_NUMBER,  " +
    		" pr.RIN_PUSHTIKA_NUMBER,  " +
    		" pr.EAST_BOUNDARY,  " +
    		" pr.WEST_BOUNDARY,  " +
    		" pr.NORTH_BOUNDARY,  " +
    		" pr.SOUTH_BOUNDARY,  " +
    		" pr.TOTAL_AREA,  " +
    		" pr.PLOTAREA,  " +
    		" pr.NO_OF_SHOPS,  " +
    		" pr.BUILDING_TYPE,  " +
    		" pr.NO_OF_CONST_FLOORS,  " +
    		" pr.SPLIT_WITH_ORIGINAL  " +
    		" FROM   IGRS_CERTIFIED_COPY_TXN txn, IGRS_CERCOPY_TXN_PARTY_DETAILS pd   " +
    		" ,IGRS_REG_INIT_PROPERTY_DETAILS pr  " +
    		" WHERE   " +
    		" txn.transaction_id=pd.CER_COPY_TXN_ID   " +
    		" AND txn.transaction_id=pr.PROPERTY_TXN_ID AND  " +
    		" CER_COPY_TXN_ID= ? ";
    
    public static final String IGRS_NOENCUM_CHECK_USERID="SELECT FUNCTION_TXN_ID,PARTY_FIRST_NAME,PARTY_MIDDLE_NAME,PARTY_LAST_NAME,PARTY_GENDER,PARTY_AGE, COUNTRY_ID,(SELECT COUNTRY_NAME FROM IGRS_COUNTRY_MASTER WHERE COUNTRY_ID=TXN.COUNTRY_ID),STATE_ID,(SELECT STATE_NAME FROM IGRS_STATE_MASTER WHERE STATE_ID=TXN.STATE_ID), DISTRICT_ID,(SELECT DISTRICT_NAME FROM IGRS_DISTRICT_MASTER WHERE DISTRICT_ID=TXN.DISTRICT_ID),PARTY_ADDRESS,POSTAL_CODE,PHONE_NUMBER,MOBILE_NUMBER,EMAIL_ID, PHOTO_PROOF_TYPE_ID,(SELECT PHOTO_PROOF_TYPE_NAME FROM IGRS_PHOTOID_PROOF_TYPE_MASTER WHERE PHOTO_PROOF_TYPE_ID=TXN.PHOTO_PROOF_TYPE_ID),PHOTO_PROOF_ID,BANK_NAME,BANK_ADDRESS,GUARDIAN_NAME, MOTHER_NAME,CASTE_ID,(SELECT CASTE_NAME FROM IGRS_CASTE_MASTER WHERE CASTE_ID=TXN.CASTE_ID),RELIGION_ID,(SELECT RELIGION_NAME FROM IGRS_RELIGION_MASTER WHERE RELIGION_ID=TXN.RELIGION_ID),NATIONALITY FROM IGRS_TXN_PARTY_DETAILS TXN WHERE PARTY_USER_ID=?";
