    public static final String GET_PENDING_APPLICATIONS_DETLS = "SELECT WT.TRANSACTION_ID,  WP.PAID_AMOUNT,(WP.PAYABLE_AMOUNT-WP.PAID_AMOUNT) DUE_AMOUNT,  WP.UPDATE_DATE,TO_CHAR((WT.CREATED_DATE),'dd/mm/yyyy') CREATED_DATE,WP.PAYABLE_AMOUNT"+
    		" FROM IGRS_CERTIFIED_COPY_TXN WT,(SELECT IGRS_CERT_COPY_PAYMENT_DTLS.CERT_TXN_ID,SUM(IGRS_CERT_COPY_PAYMENT_DTLS.PAID_AMOUNT) PAID_AMOUNT,MAX(IGRS_CERT_COPY_PAYMENT_DTLS.PAYABLE_AMOUNT) PAYABLE_AMOUNT,TO_CHAR(MAX(IGRS_CERT_COPY_PAYMENT_DTLS.UPDATE_DATE), 'dd/mm/yyyy') UPDATE_DATE"+
    		" FROM IGRS_CERT_COPY_PAYMENT_DTLS where IGRS_CERT_COPY_PAYMENT_DTLS.PAYMENT_FLAG !='C' GROUP BY IGRS_CERT_COPY_PAYMENT_DTLS.CERT_TXN_ID) WP WHERE WP.CERT_TXN_ID = WT.TRANSACTION_ID AND WT.TRANSACTION_STATUS = 'U' AND WT.TYPE_REQ='No-Encumbrance' AND WT.CREATED_BY='";
    		
