	public static String SUB_WISE_STAMP_REGFEE_REPORT = "SELECT TWO.EXEMPTION_NAME,ROUND(NVL(SUM(ONE.T1),'0'),2), ROUND(NVL(SUM(ONE.T2),'0'),2), " + "ROUND(NVL(SUM(ONE.T3),'0'),2), ROUND(NVL(SUM(ONE.T4),'0'),2), ROUND(NVL(SUM(ONE.T5),'0'),2) " + ",ROUND(NVL(SUM(ONE.T6),'0'),2), ROUND(NVL(SUM(ONE.T7),'0'),2), ROUND(NVL(SUM(ONE.T8),'0'),2) " + "FROM(" + "select B.EXEMPTION_ID AS T0,COUNT(C.REG_TXN_ID) AS T1,SUM(A.TOTAL-A.TOTAL_AFTER_EXEMP) AS T2, " + "SUM(A.REG_FEE-A.REG_FEE_AFTER_EXEMP) AS T3,SUM((A.TOTAL-A.TOTAL_AFTER_EXEMP)+(A.REG_FEE-A.REG_FEE_AFTER_EXEMP))  AS T4 " + ",0 AS T5,0 AS T6,0 AS T7,0 AS T8 FROM IGRS_DUTY_TXN_DETLS A,IGRS_DUTY_EXEMPTION_DETLS B,IGRS_REG_TXN_DETLS C " + "WHERE C.DUTY_TXN_ID=A.DUTY_TXN_ID  AND A.DUTY_TXN_ID=B.DUTY_TXN_ID AND TO_CHAR(A.CREATED_DATE,'MM/YYYY')=? " + "GROUP BY B.EXEMPTION_ID " + "UNION ALL " + "select B.EXEMPTION_ID AS T0,0 AS T1,0 AS T2,0 AS T3,0 AS T4,COUNT(C.REG_TXN_ID) AS T5, " + "SUM(A.TOTAL-A.TOTAL_AFTER_EXEMP) AS T6,SUM(A.REG_FEE-A.REG_FEE_AFTER_EXEMP) AS T7, " + "SUM((A.TOTAL-A.TOTAL_AFTER_EXEMP)+(A.REG_FEE-A.REG_FEE_AFTER_EXEMP))  AS T8 " + "FROM IGRS_DUTY_TXN_DETLS A,IGRS_DUTY_EXEMPTION_DETLS B,IGRS_REG_TXN_DETLS C " + "WHERE C.DUTY_TXN_ID=A.DUTY_TXN_ID  AND A.DUTY_TXN_ID=B.DUTY_TXN_ID " + "AND A.CREATED_DATE BETWEEN TO_DATE(?,'MM/YYYY') AND TO_DATE(?,'MM/YYYY') " + "GROUP BY B.EXEMPTION_ID)ONE ,IGRS_EXEMPTION_MASTER TWO " + "WHERE ONE.T0=TWO.EXEMPTION_ID " + "GROUP BY TWO.EXEMPTION_NAME ";
	
	public static String TOT_SUB_WISE_STAMP_REGFEE_REPORT = "SELECT SUM(TOT1),SUM(TOT2),SUM(TOT3),SUM(TOT4),SUM(TOT5),SUM(TOT6),SUM(TOT7),SUM(TOT8) " + "FROM( " + "SELECT TWO.EXEMPTION_NAME,ROUND(NVL(SUM(ONE.T1),'0'),2) AS TOT1, ROUND(NVL(SUM(ONE.T2),'0'),2) AS TOT2, " + "ROUND(NVL(SUM(ONE.T3),'0'),2) AS TOT3, ROUND(NVL(SUM(ONE.T4),'0'),2) AS TOT4, ROUND(NVL(SUM(ONE.T5),'0'),2) AS TOT5 " + ",ROUND(NVL(SUM(ONE.T6),'0'),2) AS TOT6, ROUND(NVL(SUM(ONE.T7),'0'),2) AS TOT7, ROUND(NVL(SUM(ONE.T8),'0'),2)  AS TOT8 " + "FROM(select B.EXEMPTION_ID AS T0,COUNT(C.REG_TXN_ID) AS T1,SUM(A.TOTAL-A.TOTAL_AFTER_EXEMP) AS T2, " + "SUM(A.REG_FEE-A.REG_FEE_AFTER_EXEMP) AS T3,SUM((A.TOTAL-A.TOTAL_AFTER_EXEMP)+(A.REG_FEE-A.REG_FEE_AFTER_EXEMP))  AS T4 " + ",0 AS T5,0 AS T6,0 AS T7,0 AS T8 FROM IGRS_DUTY_TXN_DETLS A,IGRS_DUTY_EXEMPTION_DETLS B,IGRS_REG_TXN_DETLS C " + "WHERE C.DUTY_TXN_ID=A.DUTY_TXN_ID  AND A.DUTY_TXN_ID=B.DUTY_TXN_ID AND TO_CHAR(A.CREATED_DATE,'MM/YYYY')=? " + "GROUP BY B.EXEMPTION_ID " + "UNION ALL " + "select B.EXEMPTION_ID AS T0,0 AS T1,0 AS T2,0 AS T3,0 AS T4,COUNT(C.REG_TXN_ID) AS T5, " + "SUM(A.TOTAL-A.TOTAL_AFTER_EXEMP) AS T6,SUM(A.REG_FEE-A.REG_FEE_AFTER_EXEMP) AS T7, " + "SUM((A.TOTAL-A.TOTAL_AFTER_EXEMP)+(A.REG_FEE-A.REG_FEE_AFTER_EXEMP))  AS T8 " + "FROM IGRS_DUTY_TXN_DETLS A,IGRS_DUTY_EXEMPTION_DETLS B,IGRS_REG_TXN_DETLS C " + "WHERE C.DUTY_TXN_ID=A.DUTY_TXN_ID  AND A.DUTY_TXN_ID=B.DUTY_TXN_ID " + "AND A.CREATED_DATE BETWEEN TO_DATE(?,'MM/YYYY') AND TO_DATE(?,'MM/YYYY') " + "GROUP BY B.EXEMPTION_ID)ONE,IGRS_EXEMPTION_MASTER TWO " + "WHERE ONE.T0=TWO.EXEMPTION_ID " + "GROUP BY TWO.EXEMPTION_NAME)";
	
