	public static String ONLY_DATE_UPDATED = "SELECT " + "  dm.DISTRICT_NAME , " + "  SYSDATE , " + "  NVL(sc,0) sc , " + "  NVL(merge_ids,'[' " + "  ||'-' " + "  ||',' " + "  || '-' " + "  ||',' " + "  ||'-' " + "  ||',' " + "  ||'-' " + "  ||']~') merge_ids , " + "  NVL(ss,0) ss, " + "  NVL(sg,0) sg , " + "  NVL( sn,0) sn, " + "  NVL(su,0) su, " + "  NVL(st,0) st , " + "  NVL(sr,0) sr , " + "  dm.district_id , " + "  dm.H_DISTRICT_NAME , " + "  NVL(sumTotal,0) sumTotal , " + "  NVL(TEHSIL_NAME, 'NIL') TEHSIL_NAME , " + "  NVL(H_TEHSIL_NAME, 'NIL') H_TEHSIL_NAME , " + "  NVL(Tehsil_id , '0')Tehsil_id " + "FROM " + "  ( " + "    SELECT " + "      Q2.DISTRICT_NAME , " + "      SYSDATE , " + "      Q2.TOT_CNT sc , " + "      Q1.merge_ids merge_ids , " + "      NVL(REPLACE(Q2.TOTAL_STAMP_DUTY,'-',''),0) ss , " + "      NVL(REPLACE(Q2.TOTAL_GRAM_DUTY,'-',''),0) sg , " + "      NVL(REPLACE(Q2.TOTAL_NAGAR_DUTY,'-',''),0) sn, " + "      NVL(REPLACE(Q2.TOTAL_UPKAR_DUTY,'-',''),0) su , " + "      NVL(REPLACE(Q2.TOTAL_DUTY,'-',''),0) st , " + "      NVL(REPLACE(Q2.TOTAL_REG_FEE_DUTY,'-',''),0) sr, " + "      Q2.DISTRICT_ID , " + "      Q2.H_DISTRICT_NAME , " + "      NVL(REPLACE((Q2.TOTAL_DUTY + Q2.TOTAL_REG_FEE_DUTY),'-',''),0) AS " + "      sumTotal , " + "      Q1.TEHSIL_NAME TEHSIL_NAME , " + "      Q1.H_TEHSIL_NAME H_TEHSIL_NAME , " + "      Q1.Tehsil_id Tehsil_id " + "    FROM " + "      ( " + "        SELECT " + "          TEHSIL_NAME, " + "          REGDATE, " + "          Tehsil_id, " + "          LISTAGG ( '[' " + "          || DEEDCNT " + "          || ',' " + "          || DEED_TYPE_NAME " + "          || ',' " + "          || DEED_ID " + "          || ',' " + "          || H_DEED_TYPE_NAME " + "          || ']~') WITHIN GROUP ( " + "        ORDER BY " + "          DEEDCNT) AS merge_ids , " + "          H_TEHSIL_NAME " + "        FROM " + "          ( " + "            SELECT " + "              COUNT (*)DEEDCNT, " + "              REGDATE, " + "              DEED_TYPE_NAME, " + "              tehSil_ID, " + "              DEED_ID, " + "              H_DEED_TYPE_NAME, " + "              TEHSIL_NAME , " + "              H_TEHSIL_NAME " + "            FROM " + "              MVIEW_REG_REPORT_Q1 " + "            WHERE " + "            update_date BETWEEN TO_DATE ( ?, 'dd-mm-yyyy hh24:mi:ss') " + "              AND TO_DATE ( ?, 'dd-mm-yyyy hh24:mi:ss') " + "            GROUP BY " + "              REGDATE, tehSil_ID, " + "              DEED_TYPE_NAME, " + "              DEED_ID, " + "              H_DEED_TYPE_NAME, " + "              TEHSIL_NAME , " + "              H_TEHSIL_NAME " + "          ) " + "        GROUP BY " + "          TEHSIL_NAME, " + "          REGDATE, " + "          Tehsil_id , " + "          H_TEHSIL_NAME " + "      ) " + "      Q1, " + "      ( " + "        SELECT " + "          TEHSIL_ID AS TEHSIL_ID, " + "          COUNT (REG_TXN_ID) TOT_CNT, " + "          SUM (stamp_duty) AS TOTAL_STAMP_DUTY, " + "          SUM (gram_duty)  AS TOTAL_GRAM_DUTY, " + "          SUM (nagar_duty) AS TOTAL_NAGAR_DUTY, " + "          SUM (upkar)      AS TOTAL_UPKAR_DUTY, " + "          SUM (total)      AS TOTAL_DUTY, " + "          SUM (reg_fee)    AS TOTAL_REG_FEE_DUTY , " + "          DISTRICT_NAME , " + "          DISTRICT_ID , " + "          H_DISTRICT_NAME " + "        FROM " + "          MVIEW_REG_REPORT_Q2 " +

	"        WHERE " + "          update_date BETWEEN TO_DATE (?, 'dd-mm-yyyy hh24:mi:ss') AND " + "          TO_DATE (?, 'dd-mm-yyyy hh24:mi:ss') " + "        GROUP BY " + "          TEHSIL_ID , " + "          DISTRICT_NAME , " + "          DISTRICT_ID , " + "          H_DISTRICT_NAME " + "      ) " + "      Q2 " + "    WHERE " + "      Q1.Tehsil_id = Q2.Tehsil_id " + "  ) " + "  Q3 , " + "  IGRS_DISTRICT_MASTER DM " + "WHERE " + "  Q3.district_name(+) =DM.district_name " + "AND DM.STATE_ID       =1 " + "AND DM.DISTRICT_STATUS='A'";
	
